        private void btnGuardar_Click(object sender, EventArgs e)
        {
            try
            {
                if (conexion.abrirConexion() == true)
                {
                    Clases.Servicio pServicio = new Clases.Servicio();
                    pServicio.IdServicio = Convert.ToInt32(txtServicio.Text);
                    pServicio.NombreServicio  = txtNombreServicio.Text;
                    pServicio.Horas = txtHoras.Text;
                    pServicio.Empleado = Convert.ToInt32(lookUpEdit1.Text);
                    pServicio.Genero = txtGenero.Text;

                    int resultado;
                    resultado = Clases.Servicio.AgregarServicio(conexion.conexion, pServicio);
                    if (resultado > 0)
                    {
                        txtGenero.Text="";
                        txtHoras.Clear();
                        txtNombreServicio.Clear();
                        txtServicio.Clear();
                        lookUpEdit1.Text = "";
                        sqlDataSource1.Fill();
                    }
                    conexion.cerrarConexion();
                }
                else
                {
                    MessageBox.Show("No se Conecto!");
                }
            }
            catch (Exception ex)
            {
                MessageBox.Show(ex.Message);
            }
        }

